import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io();

const App = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    socket.on('chat message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
      window.scrollTo(0, document.body.scrollHeight);
    });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue) {
      socket.emit('chat message', inputValue);
      setInputValue('');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <ul id="messages" className="list-none m-0 p-0">
        {messages.map((msg, index) => (
          <li key={index} className={index % 2 === 0 ? 'bg-gray-200' : ''}>
            {msg}
          </li>
        ))}
      </ul>
      <form
        id="form"
        className="bg-opacity-15 bg-black fixed bottom-0 left-0 right-0 flex h-12 box-border backdrop-blur-md"
        onSubmit={handleSubmit}
      >
        <input
          id="input"
          autoComplete="off"
          className="border-none px-4 flex-grow rounded-full m-3"
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button
          type="submit"
          className="bg-gray-800 text-white px-4 rounded-md m-3"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default App;
